// src/pages/Order.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api/axios";
import "../styles/style.css";

export default function Order({ cartItems, clearCart }) {
  const navigate = useNavigate();

  const [fullName, setFullName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [notes, setNotes] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!cartItems || cartItems.length === 0) {
      alert("Your cart is empty");
      return;
    }

    setLoading(true);

    try {
      // üîπ Prepare items exactly as backend expects
      const items = cartItems.map((item) => ({
        item_id: item.id,
        item_name: item.name,
        qty: item.qty,
        price: item.price,
      }));

      // üîπ Send order to backend
      const res = await api.post("/orders", {
        fullName,
        phoneNumber,
        notes,
        items,
      });

      // üîπ Navigate to confirmation page (NO DESIGN CHANGE)
      navigate("/order-confirmation", {
        state: {
          fullName,
          phoneNumber,
          notes,
          items,
          orderId: res.data.orderId,
          date: new Date(),
        },
      });

      clearCart();
    } catch (err) {
      console.error("‚ùå Order failed:", err);
      alert("Failed to place order. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="order-container">
      <h1 className="order-title">Complete Your Order</h1>

      <form className="order-form" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Full Name"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
          required
        />

        <input
          type="tel"
          placeholder="Phone Number"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          required
        />

        <textarea
          placeholder="Additional Notes (optional)"
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
        />

        <button type="submit" disabled={loading}>
          {loading ? "Placing Order..." : "Place Order"}
        </button>
      </form>
    </div>
  );
}
