// src/pages/Login.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/style.css";

export default function Login() {
  const navigate = useNavigate();

  const API_BASE = process.env.REACT_APP_API_URL || "http://localhost:5000";

  // UI mode: "login" | "signup"
  const [mode, setMode] = useState("login");

  // form fields
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // UX states
  const [rememberMe, setRememberMe] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Use localStorage if rememberMe, else sessionStorage
  const storage = useMemo(
    () => (rememberMe ? window.localStorage : window.sessionStorage),
    [rememberMe]
  );

  // If already logged in, redirect based on role
  useEffect(() => {
    const isLoggedIn =
      localStorage.getItem("isLoggedIn") === "true" ||
      sessionStorage.getItem("isLoggedIn") === "true";

    if (isLoggedIn) {
      const role =
        localStorage.getItem("userRole") || sessionStorage.getItem("userRole");
      navigate(role === "admin" ? "/admin" : "/home");
    }
  }, [navigate]);

  const validate = () => {
    if (!email || !password) return "Please enter email and password.";
    if (!/^\S+@\S+\.\S+$/.test(email)) return "Please enter a valid email.";
    if (mode === "signup" && !name.trim())
      return "Please enter your name for sign up.";
    if (mode === "signup" && password.length < 6)
      return "Password must be at least 6 characters.";
    return "";
  };

  const saveAuth = (user) => {
    // Clear both storages first so old data doesn't conflict
    localStorage.removeItem("isLoggedIn");
    localStorage.removeItem("userRole");
    localStorage.removeItem("userEmail");
    localStorage.removeItem("userName");

    sessionStorage.removeItem("isLoggedIn");
    sessionStorage.removeItem("userRole");
    sessionStorage.removeItem("userEmail");
    sessionStorage.removeItem("userName");

    storage.setItem("isLoggedIn", "true");
    storage.setItem("userRole", user?.role || "customer");
    storage.setItem("userEmail", user?.email || email);
    storage.setItem("userName", user?.name || name || "User");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    const msg = validate();
    if (msg) return setError(msg);

    setLoading(true);

    try {
      const endpoint = mode === "login" ? "/api/login" : "/api/signup";

      const payload =
        mode === "login"
          ? { email, password }
          : { name: name.trim(), email, password };

      const res = await fetch(`${API_BASE}${endpoint}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const data = await res.json();

      if (!res.ok) {
        setError(data?.message || "Something went wrong. Try again.");
        setLoading(false);
        return;
      }

      // Expecting backend to return: { user: { name, email, role }, token? }
      saveAuth(data.user);

      const role = data.user?.role || "customer";
      navigate(role === "admin" ? "/admin" : "/home");
    } catch (err) {
      console.error("Auth error:", err);
      setError("Server error. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  const toggleMode = () => {
    setError("");
    setMode((m) => (m === "login" ? "signup" : "login"));
  };

  return (
    <div className="auth-page">
      <div className="auth-card">
        <div className="auth-header">
          <h2 className="auth-title">
            {mode === "login" ? "Welcome back" : "Create account"}
          </h2>
          <p className="auth-subtitle">
            {mode === "login"
              ? "Sign in to Mostafa Restaurant"
              : "Sign up to start ordering delicious food"}
          </p>
        </div>

        <div className="auth-tabs">
          <button
            type="button"
            className={`auth-tab ${mode === "login" ? "active" : ""}`}
            onClick={() => setMode("login")}
          >
            Login
          </button>
          <button
            type="button"
            className={`auth-tab ${mode === "signup" ? "active" : ""}`}
            onClick={() => setMode("signup")}
          >
            Sign Up
          </button>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          {mode === "signup" && (
            <div className="auth-field">
              <label className="auth-label">Name</label>
              <input
                type="text"
                placeholder="Your full name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="auth-input"
                autoComplete="name"
              />
            </div>
          )}

          <div className="auth-field">
            <label className="auth-label">Email</label>
            <input
              type="email"
              placeholder="example@gmail.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="auth-input"
              autoComplete="email"
              required
            />
          </div>

          <div className="auth-field">
            <label className="auth-label">Password</label>
            <div className="auth-password">
              <input
                type={showPassword ? "text" : "password"}
                placeholder="••••••••"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="auth-input"
                autoComplete={mode === "login" ? "current-password" : "new-password"}
                required
              />
              <button
                type="button"
                className="auth-eye"
                onClick={() => setShowPassword((v) => !v)}
                aria-label="Toggle password visibility"
              >
                {showPassword ? "Hide" : "Show"}
              </button>
            </div>
          </div>

          <div className="auth-row">
            <label className="auth-checkbox">
              <input
                type="checkbox"
                checked={rememberMe}
                onChange={(e) => setRememberMe(e.target.checked)}
              />
              <span>Remember me</span>
            </label>

            {/* optional (only if you have it) */}
            <button
              type="button"
              className="auth-link"
              onClick={() => alert("Add forgot password flow later")}
            >
              Forgot password?
            </button>
          </div>

          {error && <div className="auth-error">{error}</div>}

          <button
            type="submit"
            className="auth-btn"
            disabled={loading}
          >
            {loading
              ? "Please wait..."
              : mode === "login"
              ? "Login"
              : "Sign Up"}
          </button>

          <p className="auth-footer">
            {mode === "login" ? "New here?" : "Already have an account?"}{" "}
            <button type="button" className="auth-link" onClick={toggleMode}>
              {mode === "login" ? "Create one" : "Login instead"}
            </button>
          </p>
        </form>
      </div>
    </div>
  );
}
