// src/pages/Order.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/style.css";

export default function Order() {
  const [menuData, setMenuData] = useState([]);
  const [fullName, setFullName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [notes, setNotes] = useState("");
  const [quantities, setQuantities] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();

  // -----------------------------
  // Fetch menu items
  // -----------------------------
  useEffect(() => {
    const fetchMenu = async () => {
      try {
        const url = process.env.REACT_APP_API_URL
          ? `${process.env.REACT_APP_API_URL}/api/items`
          : "http://localhost:5000/api/items";

        const res = await axios.get(url);

        // Ensure safe properties
        const safeItems = res.data.map((item) => ({
          item_id: item.item_id ?? item.id,
          item_name: item.item_name ?? item.name ?? "Unnamed Item",
          price: parseFloat(item.price ?? 0),
          category: item.category ?? "Uncategorized",
        }));

        // Group by category
        const grouped = safeItems.reduce((acc, item) => {
          const cat = item.category || "Uncategorized";
          if (!acc[cat]) acc[cat] = [];
          acc[cat].push(item);
          return acc;
        }, {});

        setMenuData(
          Object.keys(grouped).map((cat, idx) => ({
            category: { category_id: idx, category_name: cat },
            items: grouped[cat],
          }))
        );

        setLoading(false);
      } catch (err) {
        console.error("Failed to load menu:", err);
        setError("Failed to load menu items");
        setLoading(false);
      }
    };

    fetchMenu();
  }, []);

  // -----------------------------
  // Handle quantity change
  // -----------------------------
  const handleQtyChange = (itemId, value) => {
    setQuantities((prev) => ({ ...prev, [itemId]: Math.max(0, parseInt(value) || 0) }));
  };

  // -----------------------------
  // Calculate order total
  // -----------------------------
  const calculateTotal = () => {
    let total = 0;
    menuData.forEach((sec) =>
      sec.items.forEach((item) => {
        const qty = quantities[item.item_id] || 0;
        total += qty * item.price;
      })
    );
    return total.toFixed(2);
  };

  // -----------------------------
  // Submit order
  // -----------------------------
  const submitOrder = async (e) => {
    e.preventDefault();

    const itemsToSend = [];
    menuData.forEach((section) =>
      section.items.forEach((it) => {
        const qty = quantities[it.item_id] || 0;
        if (qty > 0)
          itemsToSend.push({
            item_id: it.item_id,
            item_name: it.item_name,
            qty,
            price: it.price,
          });
      })
    );

    if (!itemsToSend.length) return alert("Please select at least one item");

    const payload = {
      fullName,
      phoneNumber,
      notes,
      items: itemsToSend,
      date: new Date().toISOString(),
      orderId: Date.now(),
      total: calculateTotal(),
    };

    try {
      // Save locally
      const existingOrders = JSON.parse(localStorage.getItem("orders")) || [];
      localStorage.setItem("orders", JSON.stringify([...existingOrders, payload]));

      // Send to backend
      const url = process.env.REACT_APP_API_URL
        ? `${process.env.REACT_APP_API_URL}/api/orders`
        : "http://localhost:5000/api/orders";
      await axios.post(url, payload);

      // Navigate to confirmation page
      navigate("/order-confirmation", { state: payload });

      // Reset form
      setFullName("");
      setPhoneNumber("");
      setNotes("");
      setQuantities({});
    } catch (err) {
      console.error("Failed to submit order:", err.response || err);
      alert(err.response?.data?.message || "Failed to submit order");
    }
  };

  return (
    <div
      className="login-page"
      style={{ background: "linear-gradient(135deg, #ffd8a2ff, #ffd06bff)" }}
    >
      <div className="order-card">
        <button className="back-home-btn" onClick={() => navigate("/home")}>
          ← Back to Home
        </button>

        <h2 className="order-title">Place Your Order</h2>
        <p className="order-subtitle">Select your favorite items and confirm your order</p>

        {loading && <p>⏳ Loading menu items...</p>}
        {error && <p style={{ color: "red" }}>{error}</p>}

        {!loading && !error && (
          <form onSubmit={submitOrder} className="order-form">
            <div className="form-group">
              <label>Full Name:</label>
              <input
                type="text"
                value={fullName}
                onChange={(e) => setFullName(e.target.value)}
                placeholder="Enter your full name"
                required
              />
            </div>

            <div className="form-group">
              <label>Phone Number:</label>
              <input
                type="tel"
                value={phoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value)}
                placeholder="Enter your phone number"
                required
              />
            </div>

            {/* Menu Items */}
            {menuData.map((section) => (
              <fieldset key={section.category.category_id}>
                <legend>{section.category.category_name}</legend>
                {section.items.map((item) => (
                  <div key={item.item_id} className="menu-item-card">
                    <input
                      type="number"
                      min="0"
                      value={quantities[item.item_id] || 0}
                      onChange={(e) => handleQtyChange(item.item_id, e.target.value)}
                    />
                    <span className="menu-item-description">
                      {item.item_name} - ${parseFloat(item.price).toFixed(2)}
                    </span>
                  </div>
                ))}
              </fieldset>
            ))}

            <div className="form-group">
              <label>Additional Notes:</label>
              <textarea
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                rows="3"
                placeholder="Any special instructions?"
              />
            </div>

            <p style={{ fontWeight: "bold", marginTop: 10 }}>
              Total: ${calculateTotal()}
            </p>

            <button type="submit" className="submit-order-btn">
              Submit Order
            </button>
          </form>
        )}
      </div>
    </div>
  );
}
