import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';

// Pages
import Login from './pages/Login';
import Home from './pages/Home';
import Menu from './pages/Menu';
import CustomerInfo from './pages/CustomerInfo';
import OrderItems from './pages/OrderItems';
import About from './pages/About';
import OrderConfirmation from './pages/OrderConfirmation';
import Admin from './pages/Admin';

/* ===================================
   üîí Route Protection Components
=================================== */

// ProtectedRoute ensures user is logged in
function ProtectedRoute({ children }) {
  const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';
  return isLoggedIn ? children : <Navigate to="/login" replace />;
}

// AdminProtectedRoute ensures user is admin and logged in
function AdminProtectedRoute({ children }) {
  const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';
  const userRole = localStorage.getItem('userRole');
  return isLoggedIn && userRole === 'admin' ? children : <Navigate to="/home" replace />;
}

/* ===================================
   üåê App Component
=================================== */
export default function App() {
  return (
    <Routes>
      {/* Public Route */}
      <Route path="/login" element={<Login />} />

      {/* Protected Routes for Logged-in Users */}
      <Route
        path="/home"
        element={
          <ProtectedRoute>
            <Home />
          </ProtectedRoute>
        }
      />
      <Route
        path="/menu"
        element={
          <ProtectedRoute>
            <Menu />
          </ProtectedRoute>
        }
      />

      {/* Split Order into two steps */}
      <Route
        path="/customer-info"
        element={
          <ProtectedRoute>
            <CustomerInfo />
          </ProtectedRoute>
        }
      />
      <Route
        path="/order-items"
        element={
          <ProtectedRoute>
            <OrderItems />
          </ProtectedRoute>
        }
      />

      <Route
        path="/about"
        element={
          <ProtectedRoute>
            <About />
          </ProtectedRoute>
        }
      />
      <Route
        path="/order-confirmation"
        element={
          <ProtectedRoute>
            <OrderConfirmation />
          </ProtectedRoute>
        }
      />

      {/* Admin-only Route */}
      <Route
        path="/admin"
        element={
          <AdminProtectedRoute>
            <Admin />
          </AdminProtectedRoute>
        }
      />

      {/* Default Route: redirect unknown paths to Home */}
      <Route path="*" element={<Navigate to="/home" replace />} />
    </Routes>
  );
}
