// src/pages/Order.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/style.css";

export default function Order() {
  const navigate = useNavigate();

  // Step: 1 = Customer Info, 2 = Menu & Notes
  const [step, setStep] = useState(1);

  // Customer info
  const [fullName, setFullName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [street, setStreet] = useState("");
  const [city, setCity] = useState("");
  const [zip, setZip] = useState("");
  const [country, setCountry] = useState("Lebanon");

  // Order info
  const [menuData, setMenuData] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [notes, setNotes] = useState("");

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch menu items
  useEffect(() => {
    const fetchMenu = async () => {
      try {
        const url = process.env.REACT_APP_API_URL
          ? `${process.env.REACT_APP_API_URL}/api/items`
          : "http://localhost:5000/api/items";
        const res = await axios.get(url);
        const safeItems = res.data.map((item) => ({
          item_id: item.item_id ?? item.id,
          item_name: item.item_name ?? item.name ?? "Unnamed Item",
          price: parseFloat(item.price ?? 0),
          category: item.category ?? "Uncategorized",
        }));

        const grouped = safeItems.reduce((acc, item) => {
          const cat = item.category || "Uncategorized";
          if (!acc[cat]) acc[cat] = [];
          acc[cat].push(item);
          return acc;
        }, {});

        const menuArr = Object.keys(grouped).map((cat, idx) => ({
          category: { category_id: idx, category_name: cat },
          items: grouped[cat],
        }));

        setMenuData(menuArr);
        setLoading(false);
      } catch (err) {
        console.error("Failed to load menu:", err);
        setError("Failed to load menu items");
        setLoading(false);
      }
    };
    fetchMenu();
  }, []);

  const handleQtyChange = (itemId, value) => {
    setQuantities((prev) => ({ ...prev, [itemId]: parseInt(value) || 0 }));
  };

  // Step 1 → Step 2
  const handleNext = (e) => {
    e.preventDefault();
    if (!fullName || !phoneNumber || !street || !city) {
      return alert("Please fill in all required fields.");
    }
    // Add +961 if Lebanon
    if (country.toLowerCase() === "lebanon" && !phoneNumber.startsWith("+961")) {
      setPhoneNumber("+961 " + phoneNumber);
    }
    setStep(2);
  };

  // Step 2 → Step 1
  const handleBack = () => setStep(1);

  // Submit order
  const handleSubmit = async (e) => {
    e.preventDefault();

    const itemsToSend = [];
    menuData.forEach((section) =>
      section.items.forEach((it) => {
        const qty = parseInt(quantities[it.item_id]) || 0;
        if (qty > 0)
          itemsToSend.push({
            item_id: it.item_id,
            item_name: it.item_name,
            qty,
            price: parseFloat(it.price),
          });
      })
    );

    if (!itemsToSend.length) return alert("Please select at least one item");

    const payload = {
      customer_name: fullName,
      customer_phone: phoneNumber,
      customer_street: street,
      customer_city: city,
      customer_zip: zip || "N/A",
      customer_country: country || "Lebanon",
      customer_notes: notes || "None",
      items: itemsToSend,
      total: itemsToSend.reduce((sum, i) => sum + i.qty * i.price, 0),
      date: new Date().toISOString(),
    };

    try {
      // Save locally
      const existingOrders = JSON.parse(localStorage.getItem("orders")) || [];
      localStorage.setItem("orders", JSON.stringify([...existingOrders, payload]));

      // Send to backend
      const url = process.env.REACT_APP_API_URL
        ? `${process.env.REACT_APP_API_URL}/api/orders`
        : "http://localhost:5000/api/orders";
      const res = await axios.post(url, payload);

      navigate("/order-confirmation", { state: { ...payload, orderId: res.data.orderId } });
    } catch (err) {
      console.error("Failed to submit order:", err.response || err);
      alert(err.response?.data?.message || "Failed to submit order");
    }
  };

  return (
    <div className="login-page" style={{ background: "linear-gradient(135deg, #ffd8a2ff, #ffd06bff)" }}>
      <div className="order-card">
        {step === 1 && (
          <>
            <h2 className="order-title">Step 1: Your Information</h2>
            <p className="order-subtitle">Please enter your contact and delivery details</p>

            <form onSubmit={handleNext} className="order-form">
              <div className="form-group">
                <label>Full Name:</label>
                <input type="text" value={fullName} onChange={(e) => setFullName(e.target.value)} placeholder="Enter your full name" required />
              </div>

              <div className="form-group">
                <label>Phone Number:</label>
                <input type="tel" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} placeholder="Enter your phone number" required />
              </div>

              <div className="form-group">
                <label>Street Address:</label>
                <input type="text" value={street} onChange={(e) => setStreet(e.target.value)} placeholder="Enter street address" required />
              </div>

              <div className="form-group">
                <label>City:</label>
                <input type="text" value={city} onChange={(e) => setCity(e.target.value)} placeholder="Enter city" required />
              </div>

              <div className="form-group">
                <label>ZIP / Postal Code:</label>
                <input type="text" value={zip} onChange={(e) => setZip(e.target.value)} placeholder="ZIP / Postal Code (optional)" />
              </div>

              <div className="form-group">
                <label>Country:</label>
                <input type="text" value={country} onChange={(e) => setCountry(e.target.value)} placeholder="Country" />
              </div>

              <button type="submit" className="submit-order-btn">Next → Choose Items</button>
            </form>
          </>
        )}

        {step === 2 && (
          <>
            <button className="back-home-btn" onClick={handleBack}>← Back to Info</button>

            <h2 className="order-title">Step 2: Choose Items</h2>
            <p className="order-subtitle">Select your favorite items and add any additional notes</p>

            {loading && <p>⏳ Loading menu items...</p>}
            {error && <p style={{ color: "red" }}>{error}</p>}

            {!loading && !error && (
              <form onSubmit={handleSubmit} className="order-form">
                {menuData.map((section) => (
                  <fieldset key={section.category.category_id}>
                    <legend>{section.category.category_name}</legend>
                    {section.items.map((item) => (
                      <div key={item.item_id} className="menu-item-card">
                        <input
                          type="number"
                          min="0"
                          value={quantities[item.item_id] || 0}
                          onChange={(e) => handleQtyChange(item.item_id, e.target.value)}
                        />
                        <span className="menu-item-description">
                          {item.item_name} - ${item.price.toFixed(2)}
                        </span>
                      </div>
                    ))}
                  </fieldset>
                ))}

                <div className="form-group">
                  <label>Additional Notes:</label>
                  <textarea value={notes} onChange={(e) => setNotes(e.target.value)} rows="3" placeholder="Any special instructions?" />
                </div>

                <button type="submit" className="submit-order-btn">Submit Order</button>
              </form>
            )}
          </>
        )}
      </div>
    </div>
  );
}
