// src/pages/Order.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/style.css";

export default function Order() {
  const navigate = useNavigate();

  const [fullName, setFullName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [menuData, setMenuData] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [notes, setNotes] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMenu = async () => {
      try {
        const url = process.env.REACT_APP_API_URL
          ? `${process.env.REACT_APP_API_URL}/api/items`
          : "http://localhost:5000/api/items";

        const res = await axios.get(url);

        const grouped = res.data.reduce((acc, item) => {
          const cat = item.category || "Uncategorized";
          if (!acc[cat]) acc[cat] = [];
          acc[cat].push({
            item_id: item.id,
            item_name: item.name,
            price: parseFloat(item.price),
          });
          return acc;
        }, {});

        setMenuData(
          Object.entries(grouped).map(([name, items], i) => ({
            category: { id: i, name },
            items,
          }))
        );
        setLoading(false);
      } catch {
        setError("Failed to load menu");
        setLoading(false);
      }
    };
    fetchMenu();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!fullName || !phoneNumber) {
      return alert("Name and phone are required");
    }

    const normalizedPhone = phoneNumber.startsWith("+961")
      ? phoneNumber
      : `+961 ${phoneNumber}`;

    const items = [];
    menuData.forEach(section =>
      section.items.forEach(it => {
        const qty = Number(quantities[it.item_id]) || 0;
        if (qty > 0) {
          items.push({
            item_id: it.item_id,
            item_name: it.item_name,
            qty,
            price: it.price,
          });
        }
      })
    );

    if (!items.length) return alert("Select at least one item");

    const payload = {
      fullName,
      phoneNumber: normalizedPhone,
      notes,
      items,
    };

    try {
      const url = process.env.REACT_APP_API_URL
        ? `${process.env.REACT_APP_API_URL}/api/orders`
        : "http://localhost:5000/api/orders";

      const res = await axios.post(url, payload);
      navigate("/order-confirmation", {
        state: { ...payload, orderId: res.data.orderId },
      });
    } catch (err) {
      alert(err.response?.data?.message || "Order failed");
    }
  };

  return (
    <div className="login-page">
      <div className="order-card">
        <button className="back-home-btn" onClick={() => navigate("/")}>
          ‚Üê Back to Menu
        </button>

        <form onSubmit={handleSubmit} className="order-form">
          <input value={fullName} onChange={e => setFullName(e.target.value)} placeholder="Full Name" required />
          <input value={phoneNumber} onChange={e => setPhoneNumber(e.target.value)} placeholder="Phone Number" required />

          {loading && <p>Loading...</p>}
          {error && <p>{error}</p>}

          {!loading && menuData.map(section => (
            <fieldset key={section.category.id}>
              <legend>{section.category.name}</legend>
              {section.items.map(it => (
                <div key={it.item_id}>
                  <input
                    type="number"
                    min="0"
                    value={quantities[it.item_id] || 0}
                    onChange={e =>
                      setQuantities({ ...quantities, [it.item_id]: e.target.value })
                    }
                  />
                  {it.item_name} - ${it.price.toFixed(2)}
                </div>
              ))}
            </fieldset>
          ))}

          <textarea value={notes} onChange={e => setNotes(e.target.value)} placeholder="Notes" />
          <button type="submit">Submit Order</button>
        </form>
      </div>
    </div>
  );
}
