// src/pages/CustomerInfo.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/style.css';

export default function CustomerInfo() {
  const navigate = useNavigate();

  const [fullName, setFullName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [street, setStreet] = useState('');
  const [city, setCity] = useState('');
  const [zip, setZip] = useState('');
  const [country, setCountry] = useState('Lebanon');

  const handleNext = (e) => {
    e.preventDefault();
    if (!fullName || !phoneNumber || !street || !city) {
      return alert('Please fill in all required fields.');
    }

    const customerInfo = {
      fullName,
      phoneNumber: country === 'Lebanon' ? `+961 ${phoneNumber}` : phoneNumber,
      street,
      city,
      zip,
      country,
    };

    // Save temporarily in sessionStorage for next page
    sessionStorage.setItem('customerInfo', JSON.stringify(customerInfo));

    navigate('/order-items');
  };

  return (
    <div
      className="login-page"
      style={{ background: 'linear-gradient(135deg, #ffd8a2ff, #ffd06bff)' }}
    >
      <div className="order-card">
        <button className="back-home-btn" onClick={() => navigate('/home')}>
          ← Back to Home
        </button>

        <h2 className="order-title">Your Information</h2>
        <p className="order-subtitle">
          Please enter your contact and delivery details
        </p>

        <form onSubmit={handleNext} className="order-form">
          <div className="form-group">
            <label>Full Name:</label>
            <input
              type="text"
              value={fullName}
              onChange={(e) => setFullName(e.target.value)}
              placeholder="Enter your full name"
              required
            />
          </div>

          <div className="form-group">
            <label>Phone Number:</label>
            <input
              type="tel"
              value={phoneNumber}
              onChange={(e) => setPhoneNumber(e.target.value)}
              placeholder="Enter your phone number"
              required
            />
          </div>

          <div className="form-group">
            <label>Street Address:</label>
            <input
              type="text"
              value={street}
              onChange={(e) => setStreet(e.target.value)}
              placeholder="Enter street address"
              required
            />
          </div>

          <div className="form-group">
            <label>City:</label>
            <input
              type="text"
              value={city}
              onChange={(e) => setCity(e.target.value)}
              placeholder="Enter city"
              required
            />
          </div>

          <div className="form-group">
            <label>ZIP / Postal Code:</label>
            <input
              type="text"
              value={zip}
              onChange={(e) => setZip(e.target.value)}
              placeholder="ZIP / Postal Code (optional)"
            />
          </div>

          <div className="form-group">
            <label>Country:</label>
            <input
              type="text"
              value={country}
              onChange={(e) => setCountry(e.target.value)}
              placeholder="Country (optional)"
            />
          </div>

          <button type="submit" className="submit-order-btn">
            Next → Choose Items
          </button>
        </form>
      </div>
    </div>
  );
}
