// src/pages/Order.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/style.css";

export default function Order() {
  const navigate = useNavigate();

  // Step control: 1 = Customer Info, 2 = Choose Items
  const [step, setStep] = useState(1);

  // Customer info
  const [fullName, setFullName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [street, setStreet] = useState("");
  const [city, setCity] = useState("");
  const [zip, setZip] = useState("N/A");
  const [country, setCountry] = useState("Lebanon");
  const [notes, setNotes] = useState("");

  // Menu & quantities
  const [menuData, setMenuData] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch menu items
  useEffect(() => {
    const fetchMenu = async () => {
      try {
        const url = process.env.REACT_APP_API_URL
          ? `${process.env.REACT_APP_API_URL}/api/items`
          : "http://localhost:5000/api/items";
        const res = await axios.get(url);
        const safeItems = res.data.map((item) => ({
          item_id: item.item_id ?? item.id,
          item_name: item.item_name ?? item.name ?? "Unnamed Item",
          price: parseFloat(item.price ?? 0),
          category: item.category ?? "Uncategorized",
        }));
        const grouped = safeItems.reduce((acc, item) => {
          const cat = item.category || "Uncategorized";
          if (!acc[cat]) acc[cat] = [];
          acc[cat].push(item);
          return acc;
        }, {});
        const menuArr = Object.keys(grouped).map((cat, idx) => ({
          category: { category_id: idx, category_name: cat },
          items: grouped[cat],
        }));
        setMenuData(menuArr);
        setLoading(false);
      } catch (err) {
        console.error(err);
        setError("Failed to load menu items");
        setLoading(false);
      }
    };
    fetchMenu();
  }, []);

  const handleQtyChange = (itemId, value) => {
    setQuantities((prev) => ({ ...prev, [itemId]: parseInt(value) || 0 }));
  };

  const handleCustomerNext = () => {
    if (!fullName || !phoneNumber || !street || !city) {
      return alert("Please fill in Name, Phone, Street, and City.");
    }
    // Add +961 prefix if missing
    if (!phoneNumber.startsWith("+961")) setPhoneNumber("+961 " + phoneNumber);
    setStep(2);
  };

  const submitOrder = async (e) => {
    e.preventDefault();
    const itemsToSend = [];
    menuData.forEach((sec) =>
      sec.items.forEach((it) => {
        const qty = parseInt(quantities[it.item_id]) || 0;
        if (qty > 0) itemsToSend.push({ item_id: it.item_id, item_name: it.item_name, qty, price: it.price });
      })
    );
    if (!itemsToSend.length) return alert("Please select at least one item");

    const payload = {
      customer_name: fullName,
      customer_phone: phoneNumber,
      customer_street: street,
      customer_city: city,
      customer_zip: zip || "N/A",
      customer_country: country || "Lebanon",
      customer_notes: notes || "None",
      items: itemsToSend,
      total: itemsToSend.reduce((sum, i) => sum + i.qty * i.price, 0),
    };

    try {
      const url = process.env.REACT_APP_API_URL
        ? `${process.env.REACT_APP_API_URL}/api/orders`
        : "http://localhost:5000/api/orders";
      const res = await axios.post(url, payload);
      navigate("/order-confirmation", { state: { ...payload, orderId: res.data.orderId } });

      // Reset form
      setFullName("");
      setPhoneNumber("");
      setStreet("");
      setCity("");
      setZip("N/A");
      setCountry("Lebanon");
      setNotes("");
      setQuantities({});
      setStep(1);
    } catch (err) {
      console.error(err.response || err);
      alert(err.response?.data?.message || "Failed to submit order");
    }
  };

  return (
    <div className="login-page" style={{ background: "linear-gradient(135deg, #ffd8a2ff, #ffd06bff)" }}>
      <div className="order-card">
        <button className="back-home-btn" onClick={() => navigate("/home")}>
          ← Back to Home
        </button>

        {step === 1 && (
          <>
            <h2>Customer Information</h2>
            <div className="form-group">
              <label>Full Name:</label>
              <input value={fullName} onChange={(e) => setFullName(e.target.value)} placeholder="Enter your full name" required />
            </div>
            <div className="form-group">
              <label>Phone Number (+961):</label>
              <input value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} placeholder="e.g. 80763222" required />
            </div>
            <div className="form-group">
              <label>Street:</label>
              <input value={street} onChange={(e) => setStreet(e.target.value)} placeholder="Enter street" required />
            </div>
            <div className="form-group">
              <label>City:</label>
              <input value={city} onChange={(e) => setCity(e.target.value)} placeholder="Enter city" required />
            </div>
            <div className="form-group">
              <label>ZIP:</label>
              <input value={zip} onChange={(e) => setZip(e.target.value)} placeholder="ZIP code (optional)" />
            </div>
            <div className="form-group">
              <label>Country:</label>
              <input value={country} onChange={(e) => setCountry(e.target.value)} placeholder="Country" />
            </div>
            <button onClick={handleCustomerNext} className="submit-order-btn">Next: Choose Items</button>
          </>
        )}

        {step === 2 && (
          <form onSubmit={submitOrder}>
            <h2>Choose Items</h2>
            {loading && <p>⏳ Loading menu items...</p>}
            {error && <p style={{ color: "red" }}>{error}</p>}
            {!loading &&
              menuData.map((section) => (
                <fieldset key={section.category.category_id}>
                  <legend>{section.category.category_name}</legend>
                  {section.items.map((item) => (
                    <div key={item.item_id} className="menu-item-card">
                      <input
                        type="number"
                        min="0"
                        value={quantities[item.item_id] || 0}
                        onChange={(e) => handleQtyChange(item.item_id, e.target.value)}
                      />
                      <span>
                        {item.item_name} - ${item.price.toFixed(2)}
                      </span>
                    </div>
                  ))}
                </fieldset>
              ))}
            <div className="form-group">
              <label>Additional Notes:</label>
              <textarea value={notes} onChange={(e) => setNotes(e.target.value)} rows="3" placeholder="Any special instructions?" />
            </div>
            <button type="submit" className="submit-order-btn">Submit Order</button>
          </form>
        )}
      </div>
    </div>
  );
}
