// src/pages/Home.jsx
import React, { useEffect, useState, useRef } from "react";
import Cookies from "js-cookie";
import "../styles/style.css";
import Footer from "../components/Footer/Footer";
import Navbar from "../components/Navbar/Navbar";

export default function Home() {
  const [showScrollButton, setShowScrollButton] = useState(false);
  const [showCookieBanner, setShowCookieBanner] = useState(false);
  const carouselRef = useRef(null);

  // -----------------------------
  // Inject page-specific CSS
  // -----------------------------
  useEffect(() => {
    const style = document.createElement("style");
    style.innerHTML = `body { padding-top: 80px; }`;
    document.head.appendChild(style);
    return () => document.head.removeChild(style);
  }, []);

  // -----------------------------
  // Set login cookies & cookie banner
  // -----------------------------
  useEffect(() => {
    if (!Cookies.get("isLoggedIn"))
      Cookies.set("isLoggedIn", "true", { expires: 7, sameSite: "Lax" });
    if (!Cookies.get("userRole"))
      Cookies.set("userRole", "customer", { expires: 7, sameSite: "Lax" });

    if (!Cookies.get("cookieConsent"))
      setTimeout(() => setShowCookieBanner(true), 1500);
  }, []);

  // -----------------------------
  // Scroll button logic
  // -----------------------------
  useEffect(() => {
    const handleScroll = () => setShowScrollButton(window.scrollY > 300);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const scrollToTop = () => window.scrollTo({ top: 0, behavior: "smooth" });
  const acceptCookies = () => {
    Cookies.set("cookieConsent", "accepted", { expires: 365, sameSite: "Lax" });
    setShowCookieBanner(false);
  };
  const declineCookies = () => {
    Cookies.set("cookieConsent", "declined", { expires: 365, sameSite: "Lax" });
    setShowCookieBanner(false);
  };

  // -----------------------------
  // Content Data
  // -----------------------------
  const testimonials = [
    { name: "Ali H.", review: "Amazing food! Highly recommended.", rating: 5 },
    { name: "Sara K.", review: "Best restaurant in town. Friendly staff!", rating: 4.5 },
    { name: "Joseph M.", review: "Delicious dishes and fast service!", rating: 5 },
  ];

  const popularDishes = [
    "Images/appetizers.jpeg",
    "Images/BeefBurger.jpeg",
    "Images/Pizza.jpeg",
    "Images/MixedPlatter.jpeg",
    "Images/EscalopePlatter.jpeg",
    "Images/ShrimpsPlatter.jpeg",
    "Images/Desserts1.jpeg",
    "Images/I1.jpeg",
    "Images/I2.jpeg",
    "Images/I3.jpeg",
  ];

  const specialOffers = [
    { title: "Beef Burger", description: "Juicy and delicious beef burger", image: "/Images/BeefBurger.jpeg" },
    { title: "Offer Meal", description: "Special combo for you", image: "/Images/OfferMeal1.jpeg" },
  ];

  // -----------------------------
  // Carousel Arrow Handlers
  // -----------------------------
  const scrollLeft = () => carouselRef.current?.scrollBy({ left: -300, behavior: "smooth" });
  const scrollRight = () => carouselRef.current?.scrollBy({ left: 300, behavior: "smooth" });

  // -----------------------------
  // JSX Render
  // -----------------------------
  return (
    <div style={{ fontFamily: "'Poppins', sans-serif", color: "#333" }}>
      <Navbar />

      {/* Hero Section */}
      <section className="hero">
        <div className="video-wrapper">
          {["Vid1.mp4", "Vid2.mp4", "Vid3.mp4"].map((video, idx) => (
            <div key={idx} className="video-card">
              <video src={`Videos/${video}`} autoPlay muted loop />
            </div>
          ))}
        </div>
        <div className="Caption">
          <h1>Welcome to a Taste You'll Never Forget</h1>
        </div>
      </section>

      {/* Popular Dishes */}
      <section className="carousel-section">
        <h2>Popular Dishes</h2>
        <div className="carousel-wrapper">
          <button className="nav left" onClick={scrollLeft}>&#10094;</button>
          <div ref={carouselRef} className="carousel">
            {popularDishes.map((img, idx) => (
              <img key={idx} src={img} alt={`Dish ${idx}`} className="carousel-item" />
            ))}
          </div>
          <button className="nav right" onClick={scrollRight}>&#10095;</button>
        </div>
      </section>

      {/* Call To Action */}
      <section className="cta-section">
        <h2>Ready to Taste the Best?</h2>
        <p>Order online or browse our menu to explore delicious options!</p>
        <div className="cta-buttons">
          <a href="/order" className="btn order-now">Order Now</a>
          <a href="/menu" className="btn view-menu">View Menu</a>
        </div>
      </section>

      {/* Offers */}
      <section className="special-offers">
        {specialOffers.map((offer, idx) => (
          <div key={idx} className="offer-card">
            <img src={offer.image} alt={offer.title} />
            <h3>{offer.title}</h3>
            <p>{offer.description}</p>
          </div>
        ))}
      </section>

      {/* Testimonials */}
      <section className="testimonials-section">
        {testimonials.map((t, idx) => (
          <div key={idx} className="testimonial-card">
            <p>"{t.review}"</p>
            <h4>{t.name}</h4>
            <p>{'‚òÖ'.repeat(Math.floor(t.rating))}</p>
          </div>
        ))}
      </section>

      <Footer id="contact" />

      {/* Scroll to top button */}
      {showScrollButton && (
        <button className="scroll-top-btn" onClick={scrollToTop}>‚Üë</button>
      )}

      {/* Cookie Banner */}
      {showCookieBanner && (
        <div className="cookie-banner">
          <p>üç™ We use cookies to improve your experience. <a href="/privacy">Learn more</a>.</p>
          <div className="cookie-buttons">
            <button onClick={declineCookies} className="decline-btn">Decline</button>
            <button onClick={acceptCookies} className="accept-btn">Accept</button>
          </div>
        </div>
      )}
    </div>
  );
}
